const add = function(...nums) {
	return nums.reduce((total, num) => total + num);
};

const subtract = function(...nums) {
	return nums.reduce((total, num) => total - num);
};

const sum = function(nums) {
	return nums.reduce((total, num) => total + num, 0);
};

